angular.module("umbraco").controller("Umbraco.Editors.ContentEditController",function(e,t,n,r){t.create?e.content=n.getContentScaffold(t.parentId,t.doctype):e.content=n.getContent(t.id);e.saveAndPublish=function(e){e.publishDate=new Date;n.publishContent(e);r.success("Published","Content has been saved and published")};e.save=function(e){e.updateDate=new Date;n.saveContent(e);r.success("Saved","Content has been saved")}});