angular.module("umbraco").controller("Umbraco.Editors.ContentEditController",function(e,t,n){t.create?e.content=n.getContentScaffold(t.parentId,t.doctype):e.content=n.getContent(t.id);e.saveAndPublish=function(e){e.publishDate=new Date;n.publishContent(e)};e.save=function(e){e.updateDate=new Date;n.saveContent(e)}});