//this controller simply tells the dialogs service to open a mediaPicker window
//with a specified callback, this callback will receive an object with a selection on it
angular.module("umbraco").controller("mediaPickerController",function(e,t,n){function r(e){t.model.value=e.selection}t.openMediaPicker=function(e){var i=n.mediaPicker({scope:t,callback:r})}});